        foreach ($dataSet as $dataItem){
            $printer = Printer::findByModelNumber($dataItem['model_number']);
            if (!is_null($printer)){
                PrinterAttribute::addOrUpdate($printer->id, 'description', $dataItem['description']);
            }

        }
    }

