<script>
    function listOrGridView_{{ $componentId }}() {
        return {
            loaderSrc: '<div class="w-full p-4 my-32 flex justify-center items-center"><img style="max-height: 1em" alt="" src="data:image/svg+xml;base64," /></div>',
            content: '',
            ajaxURL: new URL('{{ $url }}'),
            sortingOption: '{{ array_keys($sortingOptions)[0] }}',
            containerClass: 'fotorex-list-container',
            page: {{ $page }},
            filters: {
                @foreach($filters as $filter)
                    {{ $filter->getField() }}: '{{ $filter->getValue() }}',
                @endforeach
            },
            loading: true,
            pushState: {{ $pushState ? 'true' : 'false' }},
            initialize() {
                this.$watch('sortingOption', (value) => {this.loadContent(true)});
                this.loadContent(true);
                if (this.pushState) {
                    window.addEventListener('popstate', (event) => {
                        this.page = event.state.page;
                        this.sortby = event.state.sortby;
                        if (typeof(this.$refs.filters) != 'undefined') {
                            Object.keys(event.state.params).forEach((key) => {
                                this.ajaxURL.searchParams.set(key, event.state.params[key]);
                            });
                            Array.from(this.$refs.filters.querySelectorAll('.fotorex-filter')).forEach((filter) => {
                                if (filter.getAttribute('data-filtertype') == 'text') {
                                    filter.querySelector('.fotorex-filter-text').value = event.state.params[filter.getAttribute('data-field')];
                                }
                                if (filter.getAttribute('data-filtertype') == 'range') {
                                    filter.querySelector('.range-value-input').value = event.state.params[filter.getAttribute('data-field')];
                                }
                                if (filter.getAttribute('data-filtertype') == 'checkboxgroup') {
                                    let currentFilter = event.state.params[filter.getAttribute('data-field')].split(',');
                                    Array.from(filter.querySelectorAll('.fotorex-filter-checkboxgroup')).forEach((item) => {
                                        item.checked = currentFilter.indexOf(item.value) > -1;
                                    });
                                }
                            });
                        }


                    })
                }
            },
            switchToList() {
                this.containerClass = 'fotorex-list-container'
                this.initPaginationLinks();
            },
            switchToGrid() {
                this.containerClass = 'fotorex-grid-container';
                this.initPaginationLinks();
            },
            showPage(p) {
                if (this.page != p) {
                    this.page = p;
                    this.loadContent(false);
                }
            },
            initPaginationLinks() {
                this.$nextTick(() => {
                    Array.from(document.querySelectorAll('.fotorex-list-container-pagination-link')).forEach((link) => {
                        link.addEventListener('click', (event) => {
                            this.showPage(link.getAttribute('data-page'));
                        })
                    })
                })
            },
            loadContent(resetPage) {
                this.loading = true;
                if (resetPage) {
                    this.page = 1;
                }
                this.ajaxURL.searchParams.set('sortby', this.sortingOption);
                this.ajaxURL.searchParams.set('page', this.page);
                if (typeof(this.$refs.filters) != 'undefined') {
                    Array.from(this.$refs.filters.querySelectorAll('.fotorex-filter')).forEach((filter) => {
                        if (filter.getAttribute('data-filtertype') == 'text') {
                            this.ajaxURL.searchParams.set(filter.getAttribute('data-field'), filter.querySelector('.fotorex-filter-text').value);
                        }
                        if (filter.getAttribute('data-filtertype') == 'range') {
                            this.ajaxURL.searchParams.set(filter.getAttribute('data-field'), filter.querySelector('.range-value-input').value);
                        }
                        if (filter.getAttribute('data-filtertype') == 'checkboxgroup') {
                            let currentFilter = [];
                            Array.from(filter.querySelectorAll('.fotorex-filter-checkboxgroup')).forEach((item) => {
                                if (item.checked) {
                                    currentFilter.push(item.value);
                                }
                            })
                            this.ajaxURL.searchParams.set(filter.getAttribute('data-field'), currentFilter.join(','));
                        }
                    });
                }
                if (this.pushState) {
                    let state = {params: Object.fromEntries(this.ajaxURL.searchParams.entries())};
                    state.sortby = this.sortingOption;
                    state.page = this.page;
                    history.pushState(state, 'Fotorex', this.ajaxURL);
                }
                window.axios.get(this.ajaxURL)
                    .then((response) => {
                        this.content = response.data;
                        this.loading = false;
                        this.initPaginationLinks();
                    })
            }
        }
    }
</script>
